  Route _routeAnimate(halaman) {
    return PageRouteBuilder(
      pageBuilder: (context, animation, secondaryAnimation) => halaman,
      transitionsBuilder: (context, animation, secondaryAnimation, child) {
        const begin = Offset(0.0, 1.0);
        const end = Offset.zero;
        const curve = Curves.ease;

        var tween =
            Tween(begin: begin, end: end).chain(CurveTween(curve: curve));

        return SlideTransition(
          position: animation.drive(tween),
          child: child,
        );
      },
    );
  }

  // get background from api
  getBackground() async {
    var request =
        http.Request('GET', Uri.parse('http://127.0.0.1:8000/api/background'));
    var streamedResponse = await request.send();
    var response = await http.Response.fromStream(streamedResponse);
    if (response.statusCode == 200) {
      final result = jsonDecode(response.body) as List<dynamic>;
      backgrounds.addAll(result);
      print("backgrounds : ${backgrounds}");
      for (var element in backgrounds) {
        if (element['status'] == 'aktif') {
          background_.add(element["image"]);
        }
      }
      // print("element serial key : ${key}");
    } else {
      print(response.reasonPhrase);
    }
  }

