  Future<void> _showMyDialog() async {
    return showDialog<void>(
      context: context,
      barrierDismissible: false, // user must tap button!
      builder: (BuildContext context) {
        return AlertDialog(
          title: const Text('Selesai Edit'),
          content: const SingleChildScrollView(
            child: ListBody(
              children: <Widget>[
                Text(
                    'Apakah anda yakin inign memproses\ngambar untuk siap cetak ?'),
              ],
            ),
          ),
          actions: <Widget>[
            TextButton(
              child: const Text('Tidak'),
              onPressed: () {
                // _showMyDialogPin(pin);
                Navigator.of(context).pop();
              },
            ),
            TextButton(
              child: const Text('Iya'),
              onPressed: () {
                Navigator.of(context).pop();
                setState(() {
                  isVisibleIfPrint = !isVisibleIfPrint;
                });
                screenshotController
                    .capture(delay: Duration(milliseconds: 10))
                    .then((capturedImage) async {
                  // ...
                  final directory = (await getApplicationDocumentsDirectory())
                      .path; //from path_provide package
                  var fileName = "\\${DateTime.now().microsecondsSinceEpoch}";
                  var path = '$directory';

                  screenshotController
                      .captureAndSave(
                        path, // set path where screenshot will be saved
                        fileName: "raw-image-before-crop-$fileName.png",
                      )
                      .then((value) => {
                            print(
                                "value : ${value.toString().replaceAll(r'\', r'/').toString().replaceAll(r'//', r'/')}"),
                            _uploadImage(value
                                .toString()
                                .replaceAll(r'\', r'/')
                                .toString()
                                .replaceAll(r'//', r'/')),
                          });

                  // ..
                  setState(() {
                    _imageFile = capturedImage;
                  });
                }).catchError(
                  (onError) {
                    print(onError);
                  },
                );
              },
            ),
          ],
        );
      },
    );
  }

  // dialog builder
  Future<void> _dialogBuilder(BuildContext context, title, content, stage) {
    return showDialog<void>(
      context: context,
      builder: (BuildContext context) {
        return Container(
          width: 100,
          height: 100,
          color: Colors.transparent,
          child: Card(
            color: Colors.transparent,
            // color: Colors.lightBlue,
            child: Padding(
              padding: EdgeInsets.all(0),
              child: AlertDialog(
                backgroundColor: Color.fromARGB(255, 196, 75, 146),
                title: Padding(
                  padding: const EdgeInsets.only(top: 40, bottom: 50),
                  child: Text(
                    title,
                    style: TextStyle(
                      fontSize: 56,
                      color: Colors.white,
                      fontWeight: FontWeight.bold,
                    ),
                    textAlign: TextAlign.center,
                  ),
                ),
                content: Padding(
                  padding: const EdgeInsets.all(40.0),
                  child: Text(
                    content,
                    style: TextStyle(
                      fontSize: 30,
                      color: Colors.white,
                      fontWeight: FontWeight.bold,
                    ),
                    textAlign: TextAlign.center,
                  ),
                ),
                actions: <Widget>[
                  // MainAxisAlignment.spaceAround,
                  stage == 1
                      ? OutlinedButton(
                          style: TextButton.styleFrom(
                            textStyle: Theme.of(context).textTheme.labelLarge,
                            backgroundColor: Color.fromARGB(255, 255, 72, 72),
                          ),
                          child: Padding(
                            padding: const EdgeInsets.all(15.0),
                            child: Text(
                              'Kembali'.toUpperCase(),
                              style: TextStyle(
                                fontSize: 30,
                                color: Colors.white,
                                fontWeight: FontWeight.bold,
                              ),
                            ),
                          ),
                          onPressed: () {
                            Navigator.of(context).pop();
                          },
                        )
                      : Container(),
                  Spacer(),
                  stage == 1
                      ? OutlinedButton(
                          style: TextButton.styleFrom(
                            textStyle: Theme.of(context).textTheme.labelLarge,
                            backgroundColor: Colors.orange,
                          ),
                          child: Padding(
                            padding: const EdgeInsets.all(15.0),
                            child: Text(
                              'Proses cetak'.toUpperCase(),
                              style: TextStyle(
                                fontSize: 30,
                                color: Colors.white,
                                fontWeight: FontWeight.bold,
                              ),
                            ),
                          ),
                          onPressed: () {
                            // pdf / print preview

                            setState(() {
                              isVisibleIfPrint = !isVisibleIfPrint;
                            });
                            screenshotController
                                .capture(delay: Duration(milliseconds: 10))
                                .then((capturedImage) async {
                              // ...
                              final directory =
                                  (await getApplicationDocumentsDirectory())
                                      .path; //from path_provide package
                              var fileName =
                                  "\\${DateTime.now().microsecondsSinceEpoch}";
                              var path = '$directory';

                              screenshotController
                                  .captureAndSave(
                                    path, //set path where screenshot will be saved
                                    fileName: "$fileName.png",
                                  )
                                  .then((value) => {
                                        print(
                                            "value : ${value.toString().replaceAll(r'\', r'/').toString().replaceAll(r'//', r'/')}"),
                                        _uploadImage(value
                                            .toString()
                                            .replaceAll(r'\', r'/')
                                            .toString()
                                            .replaceAll(r'//', r'/')),
                                      });

                              // ..setState
                              setState(() {
                                _imageFile = capturedImage;
                              });

                              // ... printing preview
                            }).catchError((onError) {
                              print(onError);
                            });
                          },
                        )
                      : OutlinedButton(
                          style: TextButton.styleFrom(
                            textStyle: Theme.of(context).textTheme.labelLarge,
                            backgroundColor: Colors.orange,
                          ),
                          child: Padding(
                            padding: const EdgeInsets.all(15.0),
                            child: Text(
                              'Selesai'.toUpperCase(),
                              style: TextStyle(
                                fontSize: 30,
                                color: Colors.white,
                                fontWeight: FontWeight.bold,
                              ),
                            ),
                          ),
                          onPressed: () {
                            Navigator.push(
                              context,
                              PageTransition(
                                  type: PageTransitionType.fade,
                                  child: HalamanAwal(
                                    backgrounds: backgrounds,
                                  ),
                                  inheritTheme: true,
                                  ctx: context),
                            );
                          },
                        ),
                ],
              ),
            ),
          ),
        );
      },
    );
  }

