          });
          return value.close();
        },
      );

      if (title.toString().contains("Paket B") ||
          title.toString().contains("Collage B") ||
          title.toString().contains("Collage b")) {
        // ...
        Navigator.of(context).pop();

        setState(() {
          isPrintB1 = true;
        });
        if (isPrintB1 == true) {
          var counter = 2;
          Timer.periodic(const Duration(seconds: 1), (timer) {
            // print(timer.tick);
            counter--;
            if (counter == 0) {
              print('Cancel timer');
              timer.cancel();
              setState(() {
                isPrintB2 = true;
              });
            }
          });

          if (isPrintB2 == true) {
            // ...
            Navigator.push(
              context,
              PageTransition(
                  type: PageTransitionType.fade,
                  child: HalamanAwal(
                    backgrounds: backgrounds,
                    header: headerImg,
                  ),
                  inheritTheme: true,
                  ctx: context),
            );
          }
        }
      } else {
        // ...
        Navigator.push(
          context,
          PageTransition(
              type: PageTransitionType.fade,
              child: HalamanAwal(
                backgrounds: backgrounds,
                header: headerImg,
              ),
              inheritTheme: true,
              ctx: context),
        );
      }
    });
