  Future<Uint8List> _generatePdf(PdfPageFormat format, String title,
      String image, double width, double height) async {
    final pdf = pw.Document(
      version: PdfVersion.pdf_1_5,
      compress: false,
    );

    // ...

    final provider = await flutterImageProvider(
        NetworkImage("${Variables.ipv4_local}/storage/uploads/print/$image"));

    // final image_ = await imageFromAssetBundle('assets/image.png');
    // var _image = MemoryImage(image);

    pdf.addPage(
      pw.Page(
        pageFormat: format,
        build: (context) {
          return pw.Flexible(
            child: pw.Center(
              child: pw.Image(
                provider,
                width: width * 1,
                // height: 5,
              ),
            ),
          );
        },
      ),
    );

    return pdf.save();
  }

